export interface Book {
  id: number
  title: string
  author: string
  genre: string
  rating: number
  total_copies: number
  available_copies: number
  description: string
  color: string
  cover: string
  isLoanedBook?: boolean
  video: string
  summary: string
}

interface AuthCredentials {
  fullName: string;
  email: string;
  password: string;
  universityId: number;
  universityCard: string;
}

interface BookParams {
  title: string;
  author: string;
  genre: string;
  rating: number;
  totalCopies: number;
  description: string;
  coverColor: string;
  coverUrl: string;
  videoUrl: string;
  summary: string;
}
